CREATE TABLE hospital_details(hospital_id serial PRIMARY KEY, name varchar(100) NOT NULL,email varchar(50) UNIQUE NOT NULL,password varchar(30) NOT NULL,contact_no varchar(10) NOT NULL,category varchar(10) NOT NULL, address varchar(150) NOT NULL, latitude real NOT NULL, longitude real NOT NULL, services text[] NOT NULL,timings varchar(15) NOT NULL,website varchar(80) NOT NULL,ratings real NOT NULL,created_on timestamp NOT NULL, isActive smallint NOT NULL DEFAULT 0, last_login timestamp);
