-- user_details

CREATE TABLE user_details(user_id serial PRIMARY KEY, name varchar(20) NOT NULL,email varchar(30) UNIQUE NOT NULL,password varchar(30) NOT NULL,contact_no varchar(10) NOT NULL,emergency_contact_no varchar(10) NOT NULL, blood_group varchar(5) NOT NULL,dob date NOT NULL,gender varchar(10) NOT NULL,height smallint, weight smallint,allergies varchar(50), address varchar(50) NOT NULL, pincode varchar(6) NOT NULL,created_on timestamp NOT NULL,last_login timestamp);
SELECT * from user_details;

-- service_feedback

CREATE TABLE service_feedback(service_feedback_id serial PRIMARY KEY, user_id INT NOT NULL,initiative varchar(5) NOT NULL,ratings smallint NOT NULL,liking varchar(50),suggestions varchar(50),recommend varchar(5) NOT NULL,submitted_on timestamp NOT NULL);
SELECT * from service_feedback;

-- hospital_feedback

CREATE TABLE hospital_feedback(hospital_feedback_id serial PRIMARY KEY, user_id INT NOT NULL,hospital_name varchar(50) NOT NULL, cleanliness_rating smallint NOT NULL,availability_rating smallint NOT NULL,preparedness_rating smallint NOT NULL,overall_rating smallint NOT NULL,recommend varchar(5) NOT NULL,comments varchar(50),submitted_on timestamp NOT NULL);
SELECT * from hospital_feedback;

-- user_history

CREATE TABLE user_history(user_history_id serial PRIMARY KEY, user_id INT NOT NULL,hospital_name varchar(50) NOT NULL,visited_on timestamp NOT NULL);
SELECT * from user_history;

-- ambulance_details

CREATE TABLE ambulance_details(ambulance_id serial PRIMARY KEY, operator_name varchar(20) NOT NULL,operator_contact_no varchar(10) NOT NULL,vehicle_no varchar(10) UNIQUE NOT NULL,password varchar(30) NOT NULL,type varchar(10) NOT NULL, first_aid_kit varchar(5) NOT NULL, oxygen_mask varchar(5) NOT NULL,created_on timestamp NOT NULL,last_login timestamp);
SELECT * from ambulance_details;

